mapper.select:
SELECT i.item_code ,i.item_name ,i.uom ,i.in_qty ,i.min_qty ,i.supply_price ,i.selling_price ,i.item_tax ,i.expire_date ,i.vendor_code ,common.get_vendor_name(i.vendor_code) vendor_name ,i.add_date FROM items i
AND SUBSTR(item_code, 1, 1) = #{pGroup1}
AND SUBSTR(item_code, 2, 1) = #{pGroup2}
AND SUBSTR(item_code, 3, 1) = #{pGroup3}
ORDER BY add_date DESC

mapper.update:

UPDATE items
SET   item_name = #{itemName}
      ,uom  = #{uom}
      ,in_qty = #{inQty}
      ,supply_price  = #{supplyPrice}
      ,selling_price = #{sellingPrice}
      ,item_tax  = #{itemTax}
      ,expire_date = #{expireDate}
      ,vendor_code  = #{vendorCode}
      ,min_qty = #{minQty}
WHERE  item_code = #{itemCode}

mapper.insert:
		
		insert into vendors (vendor_code, vendor_name, vendor_owner,loc_addr,
							   vendor_loc, vendor_phone, vendor_bank, vendor_account, vendor_bankowner, use_flag) 
				      values (#{vendorCode}    
                          	 ,#{vendorName}      
                             ,#{vendorOwner}      
                             ,#{locAddr}
                             ,#{vendorLoc}     
                             ,#{vendorPhone}
                             ,#{vendorBank}     
                             ,#{vendorAccount}
                             ,#{vendorBankowner}         
                             ,'Y')

mapper.update:
		UPDATE vendors
		SET vendor_owner = #{vendorOwner}
			,vendor_loc = #{vendorLoc}
			,loc_addr = #{locAddr}
			,vendor_phone = #{vendorPhone}
			,vendor_bank = #{vendorBank}
			,vendor_account = #{vendorAccount}
			,vendor_bankowner = #{vendorBankowner}
			,use_flag = #{useFlag}
		WHERE vendor_code = #{vendorCode}

mapper.insert:
		
		insert into customers (customer_code, customer_name, customer_owner, loc_addr
							   customer_loc, customer_phone, customer_pw, customer_bank, customer_account, customer_bankowner, use_flag) 
				      values (#{customerCode}    
                          	 ,#{customerName}      
                             ,#{customerOwner}      
                             ,#{locAddr}
                             ,#{customerLoc}     
                             ,#{customerPhone}
                             ,#{customerPw}
                             ,#{customerBank}     
                             ,#{customerAccount}
                             ,#{customerBankowner}         
                             ,'Y')

mapper.update:
		UPDATE customers
		SET customer_owner = #{customerOwner}
			,customer_loc = #{customerLoc}
			,customer_phone = #{customerPhone}
			,customer_pw = #{customerPw}
			,customer_bank = #{customerBank}
			,customer_account = #{customerAccount}
			,customer_bankowner = #{customerBankowner}
			,loc_addr = #{locAddr}
			,use_flag = #{useFlag}
		WHERE customer_code = #{customerCode}

UPDATE customers
SET use_flag = 'N'
WHERE customer_code = #{customerCode}

mapper.select:
SELECT customer_code 
FROM customers 
WHERE customer_code = #{customerCode}

mapper.update:
		UPDATE Sales_temp
		SET customer_owner = #{customerOwner}
			,customer_loc = #{customerLoc}
			,request_qty = #{requestQty}
			,need_date = #{needDate}
			,create_date = #{createDate}
		WHERE customer_code = #{customerCode}
